export interface I18nInterface {
    /**
     * Text of the popover to show when hovering the "list bulleted" button.
     * An empty string will disable the popover.
     */
    listBulletedPopover: string;
    /**
     * Text of the popover to show when hovering the "list numbered" button.
     * An empty string will disable the popover.
     */
    listNumberedPopover: string;
    /**
     * Text of the popover to show when hovering the "indent increase" button.
     * An empty string will disable the popover.
     */
    indentIncreasePopover: string;
    /**
     * Text of the popover to show when hovering the "indent decrease" button.
     * An empty string will disable the popover.
     */
    indentDecreasePopover: string;
}
